import { bootstrapApplication } from "@angular/platform-browser";
import { providePliantMessages, providePliantRules } from "@pliant/angular";
import { equalsRule, inheritRule, lengthRule, requiredRule, emailRule, rangeRule } from "@pliant/core";
import { AppComponent } from "./app.component";
bootstrapApplication(AppComponent, {
  providers: [providePliantRules({
    required: requiredRule(),
    email: emailRule(),
    length: lengthRule({
      min: 8,
      max: 64
    }),
    usernameLength: inheritRule("length", {
      options: {
        min: 3,
        max: 20
      },
      message: "Username must be 3-20 characters"
    }),
    ageRange: rangeRule({
      min: 18,
      max: 120,
      inclusive: true
    }),
    equals: equalsRule()
  }), providePliantMessages({
    required: "This field is required",
    email: "Please enter a valid email address",
    length: detail => {
      if (detail.actual < detail.min) {
        return `Must be at least ${detail.min} characters (currently ${detail.actual})`;
      }
      return `Must be no more than ${detail.max} characters (currently ${detail.actual})`;
    },
    range: detail => `Must be between ${detail.min} and ${detail.max}`,
    equals: "Passwords must match"
  })]
});
