import { InjectionToken } from "@angular/core";
export const PLIANT_RULES = new InjectionToken("PLIANT_RULES");
export const PLIANT_MESSAGES = new InjectionToken("PLIANT_MESSAGES");
export const providePliantRules = rules => ({
  provide: PLIANT_RULES,
  multi: true,
  useValue: rules
});
export const providePliantMessages = messages => ({
  provide: PLIANT_MESSAGES,
  multi: true,
  useValue: messages
});
